      script_object["member_docs"] = "<# restrict access to members using \/?page=member_logon #>\r\n<# apply webuser.<#[cookie.cid]#> as \"webuser\".#>\r\n\r\n\r\n<!--member docs additional content-->\r\n<h3>Member Documents<\/h3>\r\n<!--END member docs page additional content-->\r\n\r\n<# start list for member_files; \r\n    include when member_download=\"Yes\";\r\n#>\r\n    \r\n<# start header #>\r\n<table border=\u00271\u0027 cellpadding=\u00272\u0027 cellspacing=\u00270\u0027>\r\n\t<tr>\r\n\t\t<th>Name<\/th>\r\n\t\t<th>Created On<\/th>\r\n\t\t<th>Action<\/th>\r\n\t<\/tr>\r\n<# end header #>\r\n\r\n<# start row #>\r\n\t<tr>\r\n\t\t<td><# member_files.name #><\/td>\r\n\t\t<td><# member_files.created_on #><\/td>\r\n\t\t<td><a href=\u0027\/?page=member_file_download&id=<# member_files.id #>\u0027>Download<\/a> <\/td>\r\n\t<\/tr>\r\n<# end row #>\r\n\r\n<# start footer #>\r\n<\/table>\r\n<# end footer #>\r\n\r\n<# no results #>\r\n<hr \/>No Results\r\n<# end no results #>\r\n\r\n<# end list #>\r\n\r\n";
      script_object["file_list_helper"] = "<# start list for webimages; \r\ninclude when banner <> \"yes\";\r\nshow 25 rows per page;\r\n#>\r\n\r\n<# start header #>\r\n<table border=\u00271\u0027 cellpadding=\u00272\u0027 cellspacing=\u00270\u0027>\r\n\t<tr>\r\n\t\t<th>Name<\/th>\r\n\t\t<th>Created On<\/th>\r\n\t\t<th>File<\/th>\r\n\t\t<th>File Location<\/th>\r\n\t<\/tr>\r\n<# end header #>\r\n\r\n<# start row #>\r\n    \t<tr>\r\n\t\t<td><a href=\u0027\/?page=file_upload_edit&edit=<# webimages.id #>\u0027><# webimages.name #> \r\n\t\t    <span style=\u0027font-weight:normal; font-size:9pt; padding-left:10px;\u0027>[edit]<\/span><\/a><\/td>\r\n\t\t<td><# webimages.created_on #><\/td>\r\n\t\t<!--###### \r\n\t\t    When you upload an image, xxx_drive_web_url will be automatically added to your db table.\r\n\t\t        \"xxx\" is whatever you named your upload field on the input form. This is the url\r\n\t\t        that you would use to access the file from the web.\r\n\t\t######-->\r\n\t\t<td><a href=\"<# webimages.image1_drive_web_url #>\"  target=\"_blank\"><img src=\"<# webimages.image1_drive_web_url #>\"  style=\"width:125px;\" \/><\/a>\r\n\t\t    <br \/><# webimages.image1 #>\r\n\t\t<\/td>\r\n\t\t<td><# webimages.image1_drive_web_url #><\/td>\r\n\t<\/tr>\r\n<# end row #>\r\n\r\n<# start footer #>\r\n<\/table>\r\n<# end footer #>\r\n\r\n<# no results #>\r\n\t<h2>There are no files uploaded yet...<\/h2>\r\n<# end no results #>\r\n\r\n<# end list #>";
      script_object["member_logoff"] = "<# revoke access to members #>\r\n<# revoke access to admin #>\r\n<# if (\"<#[url.goto]#>\" != \"\") {\r\n\tset cookie.first to \"\";\r\n\tset cookie.last to \"\";\r\n\tset cookie.email to \"\";\r\n\tset cookie.company to \"\";\r\n\tset cookie.cid to \"\";\r\n\tredirect to \"\/<#[url.goto]#>\";\r\n} else {\r\n\tset cookie.first to \"\";\r\n\tset cookie.last to \"\";\r\n\tset cookie.email to \"\";\r\n\tset cookie.company to \"\";\r\n\tset cookie.cid to \"\";\r\n\tredirect to \"\/\";\r\n}\r\n#>\r\n";
      script_object["member_signup_confirm"] = "<# apply webuser.<#[url.mid]#> as \"mid\" .#>\r\n<# if ( \"<#[url.mid]#>\" <> \"\" ) {\r\n    \/**** \r\n        Note: you will need to adjust the from_name to your email address so the user gets the email from you.\r\n        For Google Hosting this must be the email address you signed up with.\r\n    ****\/\r\n\tsend email;\r\n\t\tfrom_name = \"support@mydomain.com\";\r\n\t\tto = \"<#[mid.email]#>\";\r\n\t\tsubject = \"Membership Confirmation\";\r\n\t\ttype = \"html\"; \/\/ text or html\r\n\t\tbody = \"<#[mid.first]#> <#[mid.last]#>,<br>\r\n\r\nWe are sending you this email to confirm your email address.<br>\r\n<br>\r\nFollow the link below to complete your member signup.<br><br>\r\n\r\n<a href=\"\/?page=member_validate&mid=<#[mid.id]#>\">Validate Your Membership<\/a><br><br>\r\n\r\nSincerely,<br>\r\n<# system.domain #> Technical Support<br><br>\";\r\n\t\t\r\n\t\t\r\n}\r\n#>\r\n\r\n<center>\r\n<div style=\"width: 94%; background-color: #FFFFFF; border: 1px solid #CCCCCC; border-radius: 3px; box-shadow: 2px 2px 2px #999999; font-family: Arial,Helvetica,sans-serif; margin-left: auto; margin-right: auto; margin-top: 10px; min-height: 250px; padding: 10px;\">\r\n\r\n<h2>Please Check Your Email<\/h2>\r\n\r\n<p style=\"font-size: 14px;\">We sent an email to <#[mid.email]#> to validate your email address for your account.<\/p>\r\n\r\n<\/div>\r\n<\/center>\r\n";
